import rclpy
from rclpy.node import Node
class PersonNode(Node):#类内部方法第一个参数都是self,代表其本身
    def __init__(self, node_name: str, name: str, age: int ) -> None:
        super().__init__(node_name)
        print("PersonNode's __init__ func is quoted")
        self.age = age
        self.name = name

    def eat(self, food_name: str):
        #print(f'my name is {self.name}, I am {self.age}, and I am eating {food_name}')
        self.get_logger().info(f'我叫{self.name},今年{self.age},正在吃{food_name}')  
def main():
    rclpy.init()
    node = PersonNode('person_node','Floating', 22)
    node.eat('牛排')
    rclpy.spin(node)
    rclpy.shutdown()
if __name__ == '__main__':
    main()