from demo_python_pkg.person_node import PersonNode
import rclpy
class WriterNode(PersonNode):#表示继承PersonNode
    def __init__(self, node_name: str, name: str, age: int, book: str) -> None:
        super().__init__(node_name, name, age)
        print('WriterNode 的 __init__ func 被调用')
        self.book = book#添加book属性
def main():
    rclpy.init()
    node = WriterNode('person_node', 'Floating', 22, 'Solivagant')#实例化WriterNode对象
    node.eat('KFC')
    rclpy.spin(node)
    rclpy.shutdown()
if __name__ == '__main__':
    main()